def clean_col(info_map, col, values):
    '''Takes in a dataset and a column index
    and transforms the values into a list of strings'''


    for idx, row in info_map.iterrows():
        info_map.iloc[idx, col] = info_map.iloc[idx, col].strip('[]')
        info_map.iloc[idx, col] = info_map.iloc[idx, col].split(',')
        int_attr = []
        for v in info_map.iloc[idx, col]:
            if v in values:
                int_attr.append(int(v))
            else:
                int_attr.append(v)     
            info_map.iloc[idx, col] = int_attr 
    return info_map

